mapper.update:
update testcases


        input_path = #{inputPath},


        input_desc = #{inputDesc},


        output_path = #{outputPath},


        output_desc = #{outputDesc}


where id = #{id}

mapper.delete:
DELETE FROM testcases
WHERE problem_id in

    #{problemId}

delete from testcases
where id in

    #{id}

mapper.select:
select id, input_path, output_path from testcases
where problem_id in

    #{problemId}
